function downloadPDF() {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();
    
    // Configurações
    const pageWidth = doc.internal.pageSize.getWidth();
    const margin = 20;
    let yPos = margin;

    try {
        // ===== VERIFICAR SE É UMA FATURA DO HISTÓRICO =====
        let cliente, itens, subtotal, valorIVA, total, ivaPercent, numeroFatura, dataFatura;

        if (window.faturaAtual) {
            // Usar dados da fatura do histórico
            cliente = window.faturaAtual.cliente;
            itens = window.faturaAtual.fatura.itens;
            subtotal = window.faturaAtual.fatura.subtotal;
            valorIVA = window.faturaAtual.fatura.iva;
            total = window.faturaAtual.fatura.total;
            ivaPercent = '16'; // Valor padrão para faturas do histórico
            numeroFatura = window.faturaAtual.fatura.numero;
            dataFatura = window.faturaAtual.fatura.data;
        } else {
            // ===== OBTER DADOS DA FATURA ATUAL (FORMULÁRIO) =====
            const clienteSelect = document.getElementById('clienteSelect');
            const clienteId = clienteSelect ? clienteSelect.value : null;
            cliente = clientes.find(c => c.id === clienteId);

            if (!cliente) {
                alert('Por favor, selecione um cliente para gerar o PDF.');
                return;
            }

            itens = obterItensFatura();
            subtotal = parseFloat(document.getElementById('subtotal').textContent);
            valorIVA = parseFloat(document.getElementById('valor-iva').textContent);
            total = parseFloat(document.getElementById('total').textContent);
            ivaPercent = document.getElementById('iva').value;

            numeroFatura = 'FAT' + new Date().getTime().toString().slice(-6);
            const dataInput = document.getElementById('dataFatura');
            dataFatura = dataInput ? dataInput.value : new Date().toISOString().split('T')[0];
        }

        // ===== CABEÇALHO COM LOGOTIPO =====
        doc.setFontSize(16);
        doc.setFont(undefined, 'bold');
        doc.setTextColor(0, 0, 0);

        // Logotipo no lado esquerdo
        const logoWidth = 25;
        const logoHeight = 25;
        const logoX = margin; // Posicionado à esquerda com margem
        const logoY = yPos;

        // Placeholder para logotipo - retângulo com texto
        // doc.setFillColor(37, 99, 235);
        // doc.rect(logoX, logoY, logoWidth, logoHeight, 'F');
        // doc.setTextColor(255, 255, 255);
        // doc.setFontSize(8);
        // doc.text('LOGOTIPO', logoX + logoWidth/2, logoY + logoHeight/2, { 
        //     align: 'center', 
        //     baseline: 'middle' 
        // });
        
        const logoBase64 = "data:image/png;base64,";
        doc.addImage(logoBase64, 'PNG', logoX, logoY, logoWidth, logoHeight);

        yPos += logoHeight + 10; // Espaço após o logotipo

        // ===== SEÇÃO DE DADOS DA EMPRESA E CLIENTE =====
        // Dados da empresa (esquerda)
        const companyX = margin;
        doc.setTextColor(0, 0, 0);
        doc.setFontSize(14);
        doc.setFont(undefined, 'bold');
        doc.text('Sigma', companyX, yPos);

        doc.setFontSize(9);
        doc.setFont(undefined, 'normal');
        doc.text('NUIT: 123456789', companyX, yPos + 6);
        doc.text('Av. Maria de Lurdes Mutola,', companyX, yPos + 12);
        doc.text('Nr. 1023, Maputo', companyX, yPos + 18);
        doc.text('Tel: +258 842525842', companyX, yPos + 24);
        doc.text('Email: Sigma@gmail.com', companyX, yPos + 30);

        // Dados do cliente (direita)
        const clientX = pageWidth - margin - 80;
        doc.setFontSize(10);
        doc.setFont(undefined, 'bold');
        doc.text('Dados do Cliente', clientX, yPos);
        doc.setFont(undefined, 'normal');
        doc.text(cliente.nome, clientX, yPos + 6);
        doc.text(`NUIT: ${cliente.nif}`, clientX, yPos + 12);

        let clientLine = 18;
        if (cliente.morada) {
            doc.text(cliente.morada, clientX, yPos + clientLine);
            clientLine += 6;
        }
        if (cliente.email) {
            doc.text(cliente.email, clientX, yPos + clientLine);
            clientLine += 6;
        }
        if (cliente.telefone) {
            doc.text(cliente.telefone, clientX, yPos + clientLine);
        }

        yPos += 40; // Espaço após as seções de dados

        // ===== LINHA SEPARADORA =====
        doc.setDrawColor(0, 0, 0);
        doc.setLineWidth(0.5);
        doc.line(margin, yPos, pageWidth - margin, yPos);
        yPos += 15;

        // ===== CABEÇALHO ORIGINAL =====
        doc.setFontSize(12);
        doc.setFont(undefined, 'bold');
        doc.text('Original', margin, yPos);
        yPos += 8;

        // ===== TABELA DE INFORMAÇÕES DA FATURA =====
        const infoHeaders = ['Número:', 'Data:', 'Vencimento:', 'Método de Pag.:'];
        const infoValues = [
            numeroFatura,
            new Date(dataFatura).toLocaleDateString('pt-PT'),
            calcularDataVencimento(dataFatura),
            'Transferência Bancária'
        ];

        // Calcular larguras das colunas
        const colWidths = [40, 40, 40, 45];
        let xPos = margin;
        
        doc.setFontSize(9);
        doc.setFont(undefined, 'bold');
        
        // Cabeçalhos
        infoHeaders.forEach((header, index) => {
            doc.text(header, xPos, yPos);
            xPos += colWidths[index];
        });

        yPos += 4;
        xPos = margin;
        doc.setFont(undefined, 'normal');
        
        // Valores
        infoValues.forEach((value, index) => {
            doc.text(value, xPos, yPos);
            xPos += colWidths[index];
        });

        yPos += 10;

        // ===== CABEÇALHO DA TABELA DE ITENS =====
        const itemHeaders = ['Descrição', 'Quantidade', 'Preço Unitário', 'Total'];
        const itemColWidths = [70, 25, 35, 30];
        
        xPos = margin;
        doc.setFont(undefined, 'bold');
        doc.setFontSize(10);
        
        itemHeaders.forEach((header, index) => {
            if (index === 1 || index === 2 || index === 3) {
                doc.text(header, xPos + itemColWidths[index] - 2, yPos, { align: 'right' });
            } else {
                doc.text(header, xPos, yPos);
            }
            xPos += itemColWidths[index];
        });

        // Linha sob o cabeçalho
        yPos += 3;
        doc.setDrawColor(0, 0, 0);
        doc.line(margin, yPos, pageWidth - margin, yPos);
        yPos += 8;

        // ===== ITENS DA FATURA =====
        doc.setFont(undefined, 'normal');
        doc.setFontSize(9);
        
        itens.forEach((item, index) => {
            // Verificar se precisa de nova página
            if (yPos > 250) {
                doc.addPage();
                yPos = 20;
                
                // Redesenhar cabeçalho da tabela na nova página
                xPos = margin;
                doc.setFont(undefined, 'bold');
                doc.setFontSize(10);
                itemHeaders.forEach((header, idx) => {
                    if (idx === 1 || idx === 2 || idx === 3) {
                        doc.text(header, xPos + itemColWidths[idx] - 2, yPos, { align: 'right' });
                    } else {
                        doc.text(header, xPos, yPos);
                    }
                    xPos += itemColWidths[idx];
                });
                yPos += 8;
                doc.setFont(undefined, 'normal');
            }
            
            xPos = margin;
            
            // Descrição
            const descricaoLines = doc.splitTextToSize(item.descricao, 65);
            const descricaoHeight = Math.max(descricaoLines.length * 4, 6);
            
            doc.text(descricaoLines, xPos, yPos);
            xPos += itemColWidths[0];
            
            // Quantidade
            doc.text(item.quantidade.toString(), xPos + itemColWidths[1] - 2, yPos, { align: 'right' });
            xPos += itemColWidths[1];
            
            // Preço Unitário
            doc.text(item.preco.toFixed(2), xPos + itemColWidths[2] - 2, yPos, { align: 'right' });
            xPos += itemColWidths[2];
            
            // Total
            doc.text(item.total.toFixed(2) + ' MZN', xPos + itemColWidths[3] - 2, yPos, { align: 'right' });
            
            yPos += descricaoHeight + 3;
            
            // Linha separadora entre itens
            if (index < itens.length - 1) {
                doc.setDrawColor(200, 200, 200);
                doc.line(margin, yPos - 4, pageWidth - margin, yPos - 4);
                yPos += 2;
            }
        });

        // ===== TOTAIS =====
        yPos += 8;
        
        // Linha acima dos totais
        doc.setDrawColor(0, 0, 0);
        doc.line(pageWidth - margin - 80, yPos - 8, pageWidth - margin, yPos - 8);
        
        // Subtotal
        doc.setFont(undefined, 'normal');
        doc.text('Subtotal:', pageWidth - margin - 70, yPos);
        doc.text(subtotal.toFixed(2), pageWidth - margin - 2, yPos, { align: 'right' });
        yPos += 5;
        
        // IVA
        doc.text(`IVA (${ivaPercent}%):`, pageWidth - margin - 70, yPos);
        doc.text(valorIVA.toFixed(2), pageWidth - margin - 2, yPos, { align: 'right' });
        yPos += 8;
        
        // Total
        doc.setFont(undefined, 'bold');
        doc.text('Total:', pageWidth - margin - 70, yPos);
        doc.text(total.toFixed(2) + ' MZN', pageWidth - margin - 2, yPos, { align: 'right' });

        // ===== LINHA SEPARADORA FINAL =====
        yPos += 15;
        doc.setDrawColor(0, 0, 0);
        doc.line(margin, yPos, pageWidth - margin, yPos);
        yPos += 12;

        // ===== DADOS BANCÁRIOS =====
        doc.setFontSize(8);
        doc.setFont(undefined, 'normal');

        doc.text('Dados Bancários:', margin, yPos);
        yPos += 4;
        doc.text('BCI', margin, yPos);
        yPos += 4;
        doc.text('CONTA: 2042 3321 01 0001', margin, yPos);
        yPos += 4;
        doc.text('NIB: 0008 0000 0423 3210 10113', margin, yPos);
        yPos += 8;

        doc.text('BIM', margin, yPos);
        yPos += 4;
        doc.text('CONTA: 595232741', margin, yPos);
        yPos += 4;
        doc.text('NIB: 0001 0000 0059 5232 74157', margin, yPos);
        yPos += 8;

        // ===== MENSAGEM FINAL =====
        doc.setFontSize(10);
        doc.setFont(undefined, 'normal');
        doc.text('Obrigado pela sua preferência!', pageWidth / 2, yPos, { align: 'center' });
        
        yPos += 5;
        doc.text('Para qualquer questão, contacte-nos através do email: liteshutter@gmail.com', 
                pageWidth / 2, yPos, { align: 'center' });

        // ===== PAGINAÇÃO =====
        yPos += 15;
        doc.setFontSize(8);
        doc.text('Página 1 de 1', pageWidth / 2, yPos, { align: 'center' });

        // ===== SALVAR PDF =====
        const nomeArquivo = `fatura_${numeroFatura}_${cliente.nome.replace(/[^a-zA-Z0-9]/g, '_')}.pdf`;
        doc.save(nomeArquivo);
        
        // Limpar fatura atual após download
        window.faturaAtual = null;
        
    } catch (error) {
        console.error('Erro ao gerar PDF:', error);
        alert('Erro ao gerar o PDF. Verifique o console para mais detalhes.');
    }
}

// Função auxiliar para obter itens da fatura (para faturas do formulário)
function obterItensFatura() {
    const itens = [];
    document.querySelectorAll('.item-row').forEach(row => {
        const descricao = row.querySelector('.item-descricao').value;
        const quantidade = parseFloat(row.querySelector('.item-quantidade').value) || 0;
        const preco = parseFloat(row.querySelector('.item-preco').value) || 0;
        
        if (descricao && quantidade > 0 && preco >= 0) {
            itens.push({
                descricao,
                quantidade,
                preco,
                total: quantidade * preco
            });
        }
    });
    return itens;
}

// Função auxiliar para calcular data de vencimento
function calcularDataVencimento(dataEmissao) {
    const data = new Date(dataEmissao);
    data.setDate(data.getDate() + 30);
    return data.toLocaleDateString('pt-PT');
}

